// Merge Jira issues with their comments
// Input: Items from "Get Comments for Each Issue" node (one item per comment)
// Output: Array of issues with comments embedded

const commentItems = $input.all();
const allIssues = $('Get ALL issues').all();

// Group comments by issue key
// Comments come from pairedItem which links back to the original issue
const commentsByIssue = {};

for (const commentItem of commentItems) {
  // Get the issue key from the pairedItem (the original issue that triggered this comment fetch)
  const pairedItem = commentItem.pairedItem;
  if (!pairedItem || pairedItem.item === undefined) continue;

  // Access the original issue data by index
  const sourceIssue = allIssues[pairedItem.item];
  if (!sourceIssue || !sourceIssue.json || !sourceIssue.json.key) continue;

  const issueKey = sourceIssue.json.key;

  // Initialize array for this issue if needed
  if (!commentsByIssue[issueKey]) {
    commentsByIssue[issueKey] = [];
  }

  // Add the comment data
  const comment = commentItem.json;
  if (comment && (comment.body || comment.author)) {
    commentsByIssue[issueKey].push({
      author: comment.author?.displayName || comment.author?.name || 'Unknown',
      body: comment.body,
      created: comment.created,
      updated: comment.updated,
      id: comment.id
    });
  }
}

// Create result with issues enriched with comments
const result = [];

for (const issueItem of allIssues) {
  const issueKey = issueItem.json.key;
  const issue = { ...issueItem.json };

  // Add comments to the issue
  const comments = commentsByIssue[issueKey] || [];
  issue.comments = comments;
  issue.commentCount = comments.length;

  result.push({ json: issue });
}

return result;

// n8n Code node: merge all incoming items into a single output array in one item

const inputItems = $input.all();
const combined = [];

// Normalize any value to an array of plain objects
function normalizeToArray(val) {
  if (typeof val === 'string') {
    const trimmed = val.trim();
    try {
      const parsed = JSON.parse(trimmed);
      return Array.isArray(parsed) ? parsed : [parsed];
    } catch {
      return [{ raw: trimmed }];
    }
  }
  if (Array.isArray(val)) return val;
  if (val && typeof val === 'object') return [val];
  return [{ value: val }];
}

// Merge all items' json into one array
for (const item of inputItems) {
  const source = item.json !== undefined ? item.json : item;
  const arr = normalizeToArray(source);
  for (const el of arr) {
    const obj = el && typeof el === 'object' ? el : { value: el };
    combined.push(obj);
  }
}

// Return a single n8n item containing the whole array
return [
  {
    json: {
      combined,            // All input collapsed into one array
      count: combined.length
    }
  }
];

// Chunk messages for Discord (2000 char limit)
const DISCORD_LIMIT = 1900; // Safe margin below 2000

function chunkMessage(text, maxLength) {
  if (text.length <= maxLength) {
    return [text];
  }

  const chunks = [];
  let remaining = text;

  while (remaining.length > 0) {
    if (remaining.length <= maxLength) {
      chunks.push(remaining);
      break;
    }

    // Try to split at newline near the limit
    let splitAt = maxLength;
    const nearbyNewline = remaining.lastIndexOf('\n', maxLength);

    if (nearbyNewline > maxLength * 0.7) {
      splitAt = nearbyNewline + 1;
    }

    chunks.push(remaining.substring(0, splitAt));
    remaining = remaining.substring(splitAt);
  }

  return chunks;
}

const items = $input.all();
const result = [];

for (const item of items) {
  const content = item.json.output || item.json.html || JSON.stringify(item.json);
  const chunks = chunkMessage(content, DISCORD_LIMIT);

  chunks.forEach((chunk, index) => {
    const partInfo = chunks.length > 1 ? `\n\n📄 Part ${index + 1}/${chunks.length}` : '';
    result.push({
      json: {
        content: chunk + partInfo,
        originalLength: content.length,
        chunkIndex: index,
        totalChunks: chunks.length
      }
    });
  });
}

return result;
